import React, { useState, useEffect, useRef } from 'react';
import './BreathingAnimation.scss';

// ZMIANA: Dodajemy prop 'isPaused'
const BreathingAnimation = ({ isPaused }) => {
  const [text, setText] = useState('Get ready...');
  const [isReady, setIsReady] = useState(false);
  const cycleTime = 8000;

  // ... (logika useEffect pozostaje bez zmian)
  useEffect(() => {
    const readyTimer = setTimeout(() => { setIsReady(true); }, 3000);
    return () => clearTimeout(readyTimer);
  }, []);

  useEffect(() => {
    if (isReady) {
      setText('Inhale');
      const textToggleInterval = setInterval(() => {
        setText(prevText => (prevText === 'Inhale' ? 'Exhale' : 'Inhale'));
      }, cycleTime / 2);
      return () => clearInterval(textToggleInterval);
    }
  }, [isReady]);

  return (
    <div className="breathing-container">
      {/* ZMIANA: Dodajemy klasÄ™ .paused, gdy komponent jest spauzowany */}
      <div className={`breathing-circle ${isReady ? 'animating' : ''} ${isPaused ? 'paused' : ''}`}></div>
      <p className="breathing-text">{text}</p>
    </div>
  );
};

export default BreathingAnimation;
