import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import BreathingAnimation from '../components/BreathingAnimation';
import ControlPanel from '../components/ControlPanel';
import './BreathingSession.scss';

const BreathingSession = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const durationInSeconds = location.state?.duration || 60;

  // === NOWE STANY DO ZARZĄDZANIA PAUZĄ I CZASEM ===
  const [isPaused, setIsPaused] = useState(false);
  const [remainingTime, setRemainingTime] = useState(durationInSeconds);

  // === NOWA, PAUZOWALNA LOGIKA TIMERA ===
  useEffect(() => {
    // Timer działa tylko wtedy, gdy NIE jest spauzowany i pozostał czas
    if (!isPaused && remainingTime > 0) {
      const interval = setInterval(() => {
        setRemainingTime(prevTime => prevTime - 1);
      }, 1000);

      // Funkcja czyszcząca, która zatrzymuje interwał po każdej zmianie
      return () => clearInterval(interval);
    }

    // Jeśli czas się skończy, wróć do ekranu ustawień
    if (remainingTime <= 0) {
      navigate('/breathe');
    }
  }, [isPaused, remainingTime, navigate]); // Efekt jest zależny od tych wartości

  // Funkcja do przełączania pauzy
  const handlePlayPause = () => {
    setIsPaused(prevState => !prevState);
  };

  return (
    <div className="session-screen">
      {/* Przekazujemy stan pauzy do animacji */}
      <BreathingAnimation isPaused={isPaused} />

      <div className="session-screen__panel-wrapper">
        <ControlPanel 
          onPlayPause={handlePlayPause}
          // Przekazujemy informację, czy sesja jest aktywna (nie spauowana)
          isPlaying={!isPaused} 
        />
      </div>
    </div>
  );
};

export default BreathingSession;
