import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import BreathingAnimation from '../components/BreathingAnimation';
import ControlPanel from '../components/ControlPanel';
import './BreathingSession.scss';

const BreathingSession = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const durationInSeconds = location.state?.duration || 60;
  const cycleTime = 8; // 8 sekund na cykl

  const [isReady, setIsReady] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [remainingTime, setRemainingTime] = useState(durationInSeconds);
  const [text, setText] = useState('Inhale');
  const [countdown, setCountdown] = useState(4); // ZMIANA 1: Startujemy od 4
  const [breathCycleSeconds, setBreathCycleSeconds] = useState(0); // ZMIANA 2: Nowy stan do synchronizacji tekstu

  // Efekt 1: Poprawiony timer do odliczania
  useEffect(() => {
    const countdownInterval = setInterval(() => {
      setCountdown(prevCountdown => {
        if (prevCountdown <= 1) {
          clearInterval(countdownInterval);
          setIsReady(true);
          return 0;
        }
        return prevCountdown - 1;
      });
    }, 1000);
    return () => clearInterval(countdownInterval);
  }, []);

  // Efekt 2: Główny, zunifikowany timer sesji
  useEffect(() => {
    if (isReady && !isPaused && remainingTime > 0) {
      const mainInterval = setInterval(() => {
        setRemainingTime(prevTime => prevTime - 1);
        // ZMIANA 2: Inkrementujemy licznik cyklu oddechu co sekundę
        setBreathCycleSeconds(prevSeconds => (prevSeconds + 1) % cycleTime);
      }, 1000);
      return () => clearInterval(mainInterval);
    }
    if (remainingTime <= 0) {
      navigate('/breathe');
    }
  }, [isReady, isPaused, remainingTime, navigate, cycleTime]);

  // Efekt 3: ZMIANA 2 - Zmiana tekstu jest teraz w pełni zsynchronizowana
  useEffect(() => {
    if (breathCycleSeconds === 0) {
      setText('Inhale');
    } else if (breathCycleSeconds === cycleTime / 2) {
      setText('Exhale');
    }
  }, [breathCycleSeconds, cycleTime]);

  const handlePlayPause = () => {
    if (isReady) {
      setIsPaused(prevState => !prevState);
    }
  };

  return (
    <div className="session-screen">
      <BreathingAnimation 
        isPaused={isPaused} 
        isReady={isReady} 
        text={text} 
        // Przekazujemy countdown tylko, gdy ma być widoczny
        countdown={countdown > 0 && countdown <= 3 ? countdown : null}
      />
      <div className="session-screen__panel-wrapper">
        <ControlPanel 
          onPlayPause={handlePlayPause}
          isPlaying={!isPaused} 
        />
      </div>
    </div>
  );
};

export default BreathingSession;
