import React from 'react';
import './BreathingAnimation.scss';

// Komponent jest teraz "głupi" - tylko wyświetla to, co dostaje w propsach
const BreathingAnimation = ({ isPaused, isReady, text }) => {
  return (
    <div className="breathing-container">
      <div 
        className={`breathing-circle ${isReady ? 'animating' : ''} ${isPaused ? 'paused' : ''}`}
      ></div>
      {/* Tekst jest teraz w pełni kontrolowany z komponentu nadrzędnego */}
      <p className="breathing-text">{isReady ? text : 'Get ready...'}</p>
    </div>
  );
};

export default BreathingAnimation;
