import React, { useState, useEffect, useRef } from 'react';
import './PerspectiveShiftScreen.scss';
import ShatterAnimation from '../components/ShatterAnimation';

const PerspectiveShiftScreen = () => {
  const [messages, setMessages] = useState([]); // Przechowuje całą rozmowę
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isFinalStep, setIsFinalStep] = useState(false); // Czy to już krok przeformułowania?
  const [finalThought, setFinalThought] = useState(''); // Przechowuje finalną, pozytywną myśl

  const chatEndRef = useRef(null);

  // Efekt do auto-scrollowania czatu
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const newUserMessage = { role: 'user', content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput('');
    setIsLoading(true);

    if (isFinalStep) {
        // To jest ostatni krok - przeformułowanie myśli
        setFinalThought(input);
        // Nie wysyłamy już do AI, tylko przechodzimy do animacji
        setTimeout(() => setMessages([]), 1500); // Czyścimy chat, aby zrobić miejsce na animację
        return;
    }

    try {
      const response = await fetch('http://localhost:5001/api/chat/perspective', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ messages: updatedMessages }),
      });
      const data = await response.json();

      const aiMessage = { role: 'assistant', content: data.ai_response };
      setMessages(prev => [...prev, aiMessage]);

      // Sprawdzamy, czy AI prosi o finalne przeformułowanie
      if (data.ai_response.includes('przeformułować')) {
        setIsFinalStep(true);
      }
    } catch (error) {
      console.error("Błąd serwera:", error);
      const errorMessage = { role: 'assistant', content: "Przepraszam, mam problem z połączeniem." };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  if (finalThought) {
    return (
      <div className="perspective-screen">
        <ShatterAnimation text={messages[0]?.content || 'Myśl'} />
        <div className="positive-thought-reveal">{finalThought}</div>
      </div>
    );
  }

  return (
    <div className="perspective-screen">
      <div className="chat-history">
        {messages.length === 0 && <p className="chat-placeholder">Jaka myśl Ci teraz ciąży?</p>}
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.role}`}>
            {msg.content}
          </div>
        ))}
        {isLoading && <div className="message assistant loading"><span>.</span><span>.</span><span>.</span></div>}
        <div ref={chatEndRef} />
      </div>
      <form onSubmit={handleSendMessage} className="thought-form chat-form">
        <textarea 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder={isFinalStep ? "Wpisz nową, zrównoważoną myśl..." : "Odpowiedz tutaj..."}
          onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage(e)}
          disabled={isLoading}
          autoFocus 
        />
        <button type="submit" disabled={isLoading || !input.trim()}>Wyślij</button>
      </form>
    </div>
  );
};

export default PerspectiveShiftScreen;
