import React, { useRef } from 'react'; // <-- ZMIANA 1
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { TransitionGroup, CSSTransition } from 'react-transition-group';

import HomeScreen from './screens/HomeScreen';
import BreatheScreen from './screens/BreatheScreen';
import BreathingSession from './screens/BreathingSession';
import Layout from './components/Layout';

const AnimatedRoutes = () => {
  const location = useLocation();
  const nodeRef = useRef(null); // <-- ZMIANA 2

  return (
    <TransitionGroup>
      {/* ZMIANA 3: Dodajemy nodeRef do CSSTransition */}
      <CSSTransition
        nodeRef={nodeRef} 
        key={location.pathname}
        classNames="fade"
        timeout={500}
        unmountOnExit // Dobra praktyka, usuwa komponent z DOM po animacji wyjścia
      >
        {/* ZMIANA 4: Owijamy Routes w div i przypinamy do niego ref */}
        <div ref={nodeRef}>
          <Routes location={location}>
            <Route element={<Layout />}>
              <Route path="/" element={<HomeScreen />} />
              <Route path="/breathe" element={<BreatheScreen />} />
            </Route>

            <Route path="/breathe/session" element={<BreathingSession />} />
          </Routes>
        </div>
      </CSSTransition>
    </TransitionGroup>
  );
};

function App() {
  return (
    <Router>
      <div className="app-container">
        <AnimatedRoutes />
      </div>
    </Router>
  );
}

export default App;
