import React, { useState, useEffect } from 'react';
import './BreathingAnimation.scss';

// ZMIANA: Komponent teraz tylko wyświetla dane
const BreathingAnimation = ({ isPaused, text }) => {
  const [isReady, setIsReady] = useState(false);

  useEffect(() => {
    const readyTimer = setTimeout(() => {
      setIsReady(true);
    }, 3000);
    return () => clearTimeout(readyTimer);
  }, []);

  return (
    <div className="breathing-container">
      <div className={`breathing-circle ${isReady ? 'animating' : ''} ${isPaused ? 'paused' : ''}`}></div>
      {/* ZMIANA: Tekst jest teraz przekazywany z góry */}
      <p className="breathing-text">{isReady ? text : 'Get ready...'}</p>
    </div>
  );
};

export default BreathingAnimation;
