import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import BreathingAnimation from '../components/BreathingAnimation';
import ControlPanel from '../components/ControlPanel';
import './BreathingSession.scss';

const BreathingSession = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const durationInSeconds = location.state?.duration || 60;

  const [isPaused, setIsPaused] = useState(true); // Zaczynamy w stanie pauzy na czas "Get ready..."
  const [remainingTime, setRemainingTime] = useState(durationInSeconds);
  const [text, setText] = useState('Inhale'); // <-- Nowy stan dla tekstu
  const cycleTime = 8000;

  // Efekt startowy po 3 sekundach
  useEffect(() => {
    const startTimer = setTimeout(() => {
      setIsPaused(false); // Uruchom animację i timery
    }, 3000);
    return () => clearTimeout(startTimer);
  }, []);

  // Główny timer sesji (pozostaje bez zmian)
  useEffect(() => {
    if (!isPaused && remainingTime > 0) {
      const interval = setInterval(() => {
        setRemainingTime(prevTime => prevTime - 1);
      }, 1000);
      return () => clearInterval(interval);
    }
    if (remainingTime <= 0) {
      navigate('/breathe');
    }
  }, [isPaused, remainingTime, navigate]);

  // NOWY EFEKT: Timer do zmiany tekstu Inhale/Exhale
  useEffect(() => {
    if (!isPaused) { // Działa tylko, gdy nie ma pauzy
      const textInterval = setInterval(() => {
        setText(prevText => (prevText === 'Inhale' ? 'Exhale' : 'Inhale'));
      }, cycleTime / 2);
      return () => clearInterval(textInterval);
    }
  }, [isPaused]); // Zależny tylko od stanu pauzy

  const handlePlayPause = () => {
    setIsPaused(prevState => !prevState);
  };

  return (
    <div className="session-screen">
      <BreathingAnimation isPaused={isPaused} text={text} />

      <div className="session-screen__panel-wrapper">
        <ControlPanel 
          onPlayPause={handlePlayPause}
          isPlaying={!isPaused} 
        />
      </div>
    </div>
  );
};

export default BreathingSession;
