import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import BreathingAnimation from '../components/BreathingAnimation';
import ControlPanel from '../components/ControlPanel';
import './BreathingSession.scss';

const BreathingSession = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const durationInSeconds = location.state?.duration || 60;
  const cycleTime = 8000;

  const [isReady, setIsReady] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [remainingTime, setRemainingTime] = useState(durationInSeconds);
  const [text, setText] = useState('Inhale');
  const [countdown, setCountdown] = useState(3); // <-- NOWY STAN DLA ODLICZANIA

  // Efekt 1: Timer do zarządzania odliczaniem 3, 2, 1
  useEffect(() => {
    const countdownInterval = setInterval(() => {
      setCountdown(prevCountdown => {
        // Gdy dojdziemy do 1, następnym krokiem będzie zakończenie odliczania
        if (prevCountdown <= 1) {
          clearInterval(countdownInterval); // Zatrzymaj ten interwał
          setIsReady(true); // Uruchom właściwą sesję
          return ''; // Zwróć pusty string, aby cyfra 1 zniknęła
        }
        return prevCountdown - 1;
      });
    }, 1000); // Odliczaj co sekundę

    return () => clearInterval(countdownInterval);
  }, []); // Uruchamia się tylko raz, po zamontowaniu komponentu

  // Efekt 2 (główny timer sesji) i Efekt 3 (timer tekstu) pozostają bez zmian...
  useEffect(() => {
    if (isReady && !isPaused && remainingTime > 0) { /* ... */ }
    if (remainingTime <= 0) { /* ... */ }
  }, [isReady, isPaused, remainingTime, navigate]);

  useEffect(() => {
    if (isReady && !isPaused) { /* ... */ }
  }, [isReady, isPaused]);

  const handlePlayPause = () => {
    if (isReady) {
      setIsPaused(prevState => !prevState);
    }
  };

  return (
    <div className="session-screen">
      <BreathingAnimation 
        isPaused={isPaused} 
        isReady={isReady} 
        text={text} 
        countdown={countdown} // <-- Przekazujemy stan odliczania
      />

      <div className="session-screen__panel-wrapper">
        <ControlPanel 
          onPlayPause={handlePlayPause}
          isPlaying={!isPaused} 
        />
      </div>
    </div>
  );
};

export default BreathingSession;
