import React from 'react';
import './BreathingAnimation.scss';

// Komponent otrzymuje nową właściwość: 'countdown'
const BreathingAnimation = ({ isPaused, isReady, text, countdown }) => {
  return (
    <div className="breathing-container">
      <div 
        className={`breathing-circle ${isReady ? 'animating' : ''} ${isPaused ? 'paused' : ''}`}
      ></div>

      {/* ZMIANA: Logika wyświetlania */}
      {/* ZMIANA: Sprawdzamy, czy countdown nie jest nullem */}
      {!isReady && countdown ? (
        <p className="countdown-text">{countdown}</p>
      ) : (
        isReady && <p className="breathing-text">{text}</p>
      )}
    </div>
  );
};

export default BreathingAnimation;
