import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './PerspectiveShiftScreen.scss';
import ShatterAnimation from '../components/ShatterAnimation';

const PerspectiveShiftScreen = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState('initialPrompt');
  const [displayText, setDisplayText] = useState('Jaka myśl Ci teraz ciąży?');
  const [showInput, setShowInput] = useState(true);
  const [textIsVisible, setTextIsVisible] = useState(true);

  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [messageHistory, setMessageHistory] = useState([]);
  const [negativeThought, setNegativeThought] = useState('');
  const [positiveThought, setPositiveThought] = useState('');

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    // Krok 1: Zapisz dane i ukryj pole wpisywania
    const newUserMessage = { role: 'user', content: input };
    const updatedHistory = [...messageHistory, newUserMessage];
    if (step === 'initialPrompt') setNegativeThought(input);
    if (step === 'finalReframe') setPositiveThought(input);
    setMessageHistory(updatedHistory);
    setShowInput(false);

    // Krok 2: Uruchom animację znikania TEKSTU
    setTextIsVisible(false);

    // Krok 3: Po animacji, pokaż wskaźnik ładowania i wyślij zapytanie
    setTimeout(() => {
      setIsLoading(true); // Pokaż "..."

      // Krok 4: Wyślij zapytanie do AI
      fetch('http://localhost:5001/api/chat/perspective', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ messages: updatedHistory }),
      })
      .then(response => response.json())
      .then(data => {
        // Krok 5: Gdy AI odpowie, ukryj wskaźnik i pokaż nowy tekst
        const aiMessage = { role: 'assistant', content: data.ai_response };
        setMessageHistory(prev => [...prev, aiMessage]);

        setIsLoading(false); // Ukryj "..."
        setDisplayText(data.ai_response);
        setTextIsVisible(true); // Pokaż odpowiedź AI

        if (data.ai_response.includes('przeformułować')) {
          setStep('finalReframe');
        } else {
          setStep('conversation');
        }
        setShowInput(true);
      })
      .catch(error => {
        // ... obsługa błędu ...
        console.error("Błąd serwera:", error);
        setIsLoading(false);
        // Możesz tu pokazać komunikat o błędzie
      });
    }, 500); // Czas musi pasować do transition w CSS
    setInput('');
  };

  // ... (reszta logiki useEffect bez zmian)

  return (
    <div className="perspective-screen">
      <div className="display-text-area">
        {/* ZMIANA: Mamy teraz 3 możliwe stany do wyświetlenia */}
        {isLoading ? (
          <div className="thinking-indicator"><span>.</span><span>.</span><span>.</span></div>
        ) : (
          <div className={`text-content ${textIsVisible ? 'visible' : 'hidden'}`}>
            {displayText}
          </div>
        )}
      </div>

      {showInput && (
        <form onSubmit={handleSendMessage} className="thought-form single-view">
          <textarea 
            value={input} 
            onChange={(e) => setInput(e.target.value)} 
            placeholder="Wpisz tutaj..."
            disabled={isLoading}
            autoFocus 
          />
          <button type="submit" disabled={isLoading || !input.trim()}>Wyślij</button>
        </form>
      )}
    </div>
  );
};

export default PerspectiveShiftScreen;
