import React, { useState, useEffect } from 'react';
import './ThoughtIntros.scss';

const exampleThoughts = [
  "...jestem do niczego.",
  "...zawsze wszystko psuję.",
  "...to się na pewno nie uda.",
  "...co oni sobie o mnie pomyślą?"
];

const ThoughtIntros = ({ onComplete }) => {
  const [index, setIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsVisible(false); // Rozpocznij znikanie
      setTimeout(() => {
        setIndex(prevIndex => {
          const nextIndex = prevIndex + 1;
          if (nextIndex >= exampleThoughts.length) {
            clearInterval(interval);
            onComplete(); // Zasygnalizuj koniec animacji
            return prevIndex;
          }
          setIsVisible(true); // Pokaż następną myśl
          return nextIndex;
        });
      }, 600); // Czas na zniknięcie
    }, 2000); // Czas wyświetlania każdej myśli

    return () => clearInterval(interval);
  }, [onComplete]);

  return (
    <div className={`intro-thought ${isVisible ? 'visible' : ''}`}>
      {exampleThoughts[index]}
    </div>
  );
};

export default ThoughtIntros;
