import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import BreathingAnimation from '../components/BreathingAnimation';
import ControlPanel from '../components/ControlPanel'; // <-- Importujemy ControlPanel
import './BreathingSession.scss'; // <-- Utworzymy nowy plik SCSS

const BreathingSession = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const durationInSeconds = location.state?.duration || 60;
  const [isPlaying, setIsPlaying] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/breathe'); // Wróć do ekranu ustawień po zakończeniu
    }, durationInSeconds * 1000);

    return () => clearTimeout(timer);
  }, [durationInSeconds, navigate]);

  const handlePlayPause = () => {
    // W tej wersji pauza po prostu wraca do poprzedniego ekranu
    setIsPlaying(false);
    navigate(-1); // Wróć o jeden ekran
  };

  return (
    <div className="session-screen">
      <BreathingAnimation />

      {/* Dodajemy ControlPanel na stałe na dole */}

        <ControlPanel 
          onPlayPause={handlePlayPause}
          isPlaying={isPlaying} 
        />

    </div>
  );
};

export default BreathingSession;
