import React, { useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { TransitionGroup, CSSTransition } from 'react-transition-group';

import HomeScreen from './screens/HomeScreen';
import BreatheScreen from './screens/BreatheScreen';
import BreathingSession from './screens/BreathingSession';
import Layout from './components/Layout';

const AnimatedRoutes = () => {
  const location = useLocation();

  // Ważne: Tworzymy osobny ref dla każdego komponentu wewnątrz Layout i dla BreathingSession
  // Ale dla uproszczenia na razie możemy użyć jednego, bo nigdy nie są renderowane jednocześnie
  const nodeRef = useRef(null); 

  return (
    // Dodajemy klasę do TransitionGroup
    <TransitionGroup className="transition-group">
      <CSSTransition
        // Używamy location.key dla lepszej unikalności przy nawigacji
        key={location.key}
        nodeRef={nodeRef} 
        timeout={500}
        classNames="fade"
        unmountOnExit
      >
        {/* Dodajemy klasę do diva i przypinamy ref */}
        <div ref={nodeRef} className="transition-wrapper">
          <Routes location={location}>
            <Route element={<Layout />}>
              <Route path="/" element={<HomeScreen />} />
              <Route path="/breathe" element={<BreatheScreen />} />
            </Route>

            <Route path="/breathe/session" element={<BreathingSession />} />
          </Routes>
        </div>
      </CSSTransition>
    </TransitionGroup>
  );
};

function App() {
  return (
    <Router>
      {/* Usunęliśmy .app-container stąd, aby nie kolidował z pozycjonowaniem */}
      <AnimatedRoutes />
    </Router>
  );
}

export default App;
