import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './ControlPanel.scss';

// Tutaj wklejasz nową, zaktualizowaną tablicę panelButtons
const panelButtons = [
  { id: 'focus', path: '/focus-reset', icon: '/assets/icons/focus.svg' },
  null, 
  { id: 'breathe', path: '/breathe', icon: '/assets/icons/breathe.svg' },
  // ... reszta tablicy
];


const ControlPanel = ({ onPlayPause, isPlaying }) => {
  const location = useLocation();

  return (
    <div className="control-panel">
      {panelButtons.map((button, index) => {

        if (index === 1) {
          // Przycisk Play/Pause pozostaje bez zmian
          return (
            <button
              key="play-pause"
              className={`panel-button play-button ${isPlaying ? 'active' : ''}`}
              onClick={onPlayPause}
            >
              ▶
            </button>
          );
        }

        return (
          <Link
            key={button.id}
            to={button.path}
            className={`panel-button ${location.pathname === button.path ? 'active' : ''}`}
          >
            {/* ZMIANA TUTAJ: 
              Zamiast {button.icon} używamy tagu <img> 
            */}
            <img src={button.icon} alt={`${button.id} icon`} className="panel-icon" />
          </Link>
        );
      })}
    </div>
  );
};

export default ControlPanel;
